import React from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { CheckCircle, Mail, Upload, Send, BarChart2 } from "lucide-react";

const LandingPage = () => {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/login');
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="min-h-screen bg-white text-gray-900">
      {/* Hero Section */}
      <section className="text-center py-20 bg-gradient-to-br from-blue-500 to-purple-600 text-white">
        <h1 className="text-5xl font-bold mb-4">Send Personalized Emails at Scale ‚Äì in Seconds</h1>
        <p className="text-xl mb-6">Upload your list, customize your message, and track results ‚Äì all from one dashboard.</p>
        <Button size="lg" className="bg-white text-blue-600 hover:bg-gray-100">Try a Live Demo</Button>
      </section>

      {/* Features Section */}
      <section className="py-16 px-6 md:px-20 bg-gray-100">
        <h2 className="text-3xl font-bold text-center mb-10">Why Choose Us?</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {[
            { icon: Upload, title: "CSV Upload", desc: "Import your recipient list with one click." },
            { icon: Mail, title: "Dynamic Personalization", desc: "Use placeholders for name, company, and more." },
            { icon: Send, title: "Automated Campaigns", desc: "Schedule and send emails at the right time." },
            { icon: BarChart2, title: "Analytics Dashboard", desc: "Track opens, clicks, and responses in real-time." },
            { icon: CheckCircle, title: "CRM Integrations", desc: "Sync with your favorite tools like Sheets or Slack." },
          ].map((f, idx) => (
            <Card key={idx} className="text-center shadow-xl">
              <CardContent className="p-6 flex flex-col items-center">
                <f.icon className="w-12 h-12 text-blue-600 mb-4" />
                <h3 className="font-semibold text-lg mb-2">{f.title}</h3>
                <p className="text-gray-700">{f.desc}</p>
              </CardContent>
            </Card>
          ))}
        </div>
      </section>

      {/* How It Works */}
      <section className="py-20 bg-white px-6 md:px-20">
        <h2 className="text-3xl font-bold text-center mb-12">How It Works</h2>
        <div className="grid md:grid-cols-3 gap-10 text-center">
          {[
            { title: "1. Upload Contacts", desc: "Upload a CSV file of your recipients easily." },
            { title: "2. Design Template", desc: "Use our editor to create dynamic email content." },
            { title: "3. Send & Track", desc: "Launch your campaign and monitor performance instantly." },
          ].map((step, idx) => (
            <div key={idx} className="p-6 border rounded-2xl shadow-sm">
              <h3 className="text-xl font-semibold mb-2">{step.title}</h3>
              <p className="text-gray-600">{step.desc}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Use Cases */}
      <section className="bg-gray-50 py-20 px-6 md:px-20">
        <h2 className="text-3xl font-bold text-center mb-10">Perfect for Every Scenario</h2>
        <div className="grid md:grid-cols-2 gap-8 text-lg">
          <ul className="space-y-4">
            <li>üéì Event invites for schools & colleges</li>
            <li>üõçÔ∏è Marketing offers and seasonal promotions</li>
            <li>üßë‚Äçüíº Internal HR announcements</li>
          </ul>
          <ul className="space-y-4">
            <li>üì£ NGO awareness and outreach</li>
            <li>üì∞ Customized newsletters for subscribers</li>
            <li>üéÅ Personalized reward or discount emails</li>
          </ul>
        </div>
      </section>

      {/* Testimonials */}
      <section className="py-20 bg-white px-6 md:px-20 text-center">
        <h2 className="text-3xl font-bold mb-10">What People Are Saying</h2>
        <blockquote className="text-xl italic text-gray-700 max-w-2xl mx-auto">
          ‚ÄúSaved us hours every week and helped us personalize thousands of emails effortlessly.‚Äù
        </blockquote>
        <p className="mt-4 text-gray-500">‚Äî Campaign Manager, Local NGO</p>
      </section>

      {/* CTA Again */}
      <section className="text-center py-20 bg-blue-600 text-white">
        <h2 className="text-3xl font-bold mb-4">Ready to Transform Your Email Outreach?</h2>
        <Button size="lg" className="bg-white text-blue-600 hover:bg-gray-100">Start Sending Now</Button>
      </section>

      {/* Footer */}
      <footer className="py-8 px-6 md:px-20 bg-gray-900 text-white text-sm text-center">
        <p>&copy; {new Date().getFullYear()} BulkMailer Pro. All rights reserved.</p>
        <div className="mt-2">
          <a href="#" className="hover:underline mx-2">Privacy Policy</a>|
          <a href="#" className="hover:underline mx-2">Terms of Service</a>
        </div>
      </footer>
    </div>
  );
}
