import React, { useState, useEffect } from 'react';
import {
  faUser,
  faEnvelope,
  faGear,
  faMoon,
  faSignOutAlt,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePlus, faCircleUser } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';
// Assuming you have a profile image in the images folder

const Home = ({ setIsAuth }) => {
  const [darkMode, setDarkMode] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const [username, setUsername] = useState('User'); // Replace with actual username logic
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };
  const handleEditProfile = () => {
    navigate('/edit-profile');
  };
  const goToCompose = () => {
    navigate('/dashboard');
    <BrowserRouter>
      <Routes>
        <Route element={isAuth ? <Dashboard setIsAuth={setIsAuth} /> : <Navigate to="/" />}></Route> // or your /compose page if you have one
      </Routes>
    </BrowserRouter>
  };
  const navigate = useNavigate();
  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="navbar">
        <span className="navbar-title">CursorMail</span>
        <div className="navbar-buttons">
          <div className="compose-button" onClick={goToCompose} title="Compose"><FontAwesomeIcon icon={faSquarePlus} /></div>
        </div>
        <div className={`profile-dropdown ${darkMode ? 'dark' : 'light'}`}>
          <div className="profile-info">
            <FontAwesomeIcon alt="Profile"
              className="avatar" icon={faCircleUser}  onClick={()=>setShowLogout(showLogout)}/>  
            
            <h4>{username}</h4>
          </div>
            

          <div className="footer">
            <a href="#">Privacy Policy</a> · <a href="#">Terms of Service</a>
          </div>
        </div>
      </nav>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search..."
          className="search-input"
        />
        <button className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md">Search</button>
      </div>
    </div>
  );
};
export default Home;
