import React, { useState, useEffect } from 'react';
import {
  faUser,
  faEnvelope,
  faGear,
  faMoon,
  faSignOutAlt,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faCircleUser } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';
// Assuming you have a profile image in the images folder

const Home = ({ setIsAuth }) => {
  const [darkMode, setDarkMode] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const [hoveredItem, setHoveredItem] = useState(null);
  const [username, setUsername] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  
  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showLogout && !event.target.closest('.profile-dropdown')) {
        setShowLogout(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogout]);

  // Fetch user data from database
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        setIsLoading(true);
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('No token found');
          setIsLoading(false);
          return;
        }

        const response = await fetch('http://localhost:5000/api/home', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUsername(data.user.username);
        } else if (response.status === 401) {
          // Token is invalid, redirect to login
          localStorage.removeItem('token');
          setIsAuth(false);
          navigate('/');
        } else {
          console.error('Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };

  const handleEmailSettings = () => {
    // Handle email settings navigation
    console.log('Email Settings clicked');
  };

  const handleSettings = () => {
    // Handle settings navigation
    console.log('Settings clicked');
  };

  const goToCompose = () => {
    navigate('/dashboard');
    <BrowserRouter>
      <Routes>
        <Route element={isAuth ? <Dashboard setIsAuth={setIsAuth} /> : <Navigate to="/" />}></Route> // or your /compose page if you have one
      </Routes>
    </BrowserRouter>
  };

  const navigate = useNavigate();

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-100'}`}>
      <nav className="navbar">
        <span className="navbar-title">CursorMail</span>
        <div className="navbar-buttons">
          <div className="compose-button" onClick={goToCompose} title="Compose"><FontAwesomeIcon icon={faPenToSquare} /></div>
        </div>
        <div className={`profile-dropdown ${darkMode ? 'dark' : 'light'}`}>
          <div className="profile-info">
            {/* Profile Icon Button */}
            <FontAwesomeIcon
              className="avatar"
              icon={faCircleUser}
              size="2x"
              onClick={() => setShowLogout(!showLogout)}
              style={{ cursor: 'pointer' }}
            />

            {/* Dropdown Menu */}
            {showLogout && (
              <div className="logout-dropdown">
                {/* Profile Header */}
                <div className="profile-header">
                  <img 
                    src={require('./images/profile.jfif')} 
                    alt="Profile" 
                    className="profile-image"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'block';
                    }}
                  />
                  <FontAwesomeIcon 
                    icon={faCircleUser} 
                    className="profile-fallback"
                    style={{ display: 'none' }}
                  />
                  <h4 className="profile-name">{username || 'Loading...'}</h4>
                  <p className="profile-followers">{followers}</p>
                </div>

                {/* Menu Items */}
                <div className="menu-items">
                  <div 
                    className={`menu-item ${hoveredItem === 'profile' ? 'hovered' : ''}`}
                    onMouseEnter={() => setHoveredItem('profile')}
                    onMouseLeave={() => setHoveredItem(null)}
                    onClick={handleEditProfile}
                  >
                    <FontAwesomeIcon icon={faUser} />
                    <span>My Profile</span>
                  </div>
                  
                  <div 
                    className={`menu-item ${hoveredItem === 'email' ? 'hovered' : ''}`}
                    onMouseEnter={() => setHoveredItem('email')}
                    onMouseLeave={() => setHoveredItem(null)}
                    onClick={handleEmailSettings}
                  >
                    <FontAwesomeIcon icon={faEnvelope} />
                    <span>Email Settings</span>
                  </div>
                  
                  <div 
                    className={`menu-item ${hoveredItem === 'settings' ? 'hovered' : ''}`}
                    onMouseEnter={() => setHoveredItem('settings')}
                    onMouseLeave={() => setHoveredItem(null)}
                    onClick={handleSettings}
                  >
                    <FontAwesomeIcon icon={faGear} />
                    <span>Settings</span>
                  </div>
                  
                  <div className="menu-item toggle-item">
                    <FontAwesomeIcon icon={faMoon} />
                    <span>Switch to Dark</span>
                    <label className="switch">
                      <input
                        type="checkbox"
                        checked={darkMode}
                        onChange={() => setDarkMode(!darkMode)}
                      />
                      <span className="slider"></span>
                    </label>
                  </div>
                  
                  <div 
                    className={`menu-item ${hoveredItem === 'logout' ? 'hovered' : ''}`}
                    onMouseEnter={() => setHoveredItem('logout')}
                    onMouseLeave={() => setHoveredItem(null)}
                    onClick={handleLogout}
                  >
                    <FontAwesomeIcon icon={faSignOutAlt} />
                    <span>Log Out</span>
                  </div>
                </div>

                {/* Footer */}
                <div className="dropdown-footer">
                  <span>Privacy Policy</span>
                  <span className="footer-dot">•</span>
                  <span>Terms of Service</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search..."
          className="search-input"
        />
        <button className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md">Search</button>
      </div>
    </div>
  );
};

export default Home;
