import React, { useState, useEffect } from 'react';
import {
  faUser,
  faGear,
  faMoon,
  faSignOutAlt,
  faInbox,
  faBan,
  faPaperPlane,
  faFileAlt,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenToSquare, faCircleUser } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';
import EditProfile from './EditProfile';
import './App.css';

const Home = ({ setIsAuth }) => {
  const [darkMode, setDarkMode] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const [hoveredItem, setHoveredItem] = useState(null);
  const [username, setUsername] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const navigate = useNavigate();
  
  // Function to fetch user data
  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No token found');
        return;
      }

      const response = await fetch('http://localhost:5000/api/home', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Home: Received user data:', data);
        setUsername(data.user.username);
        // Set profile photo URL if available
        if (data.user.profile_photo_filename) {
          setProfilePhoto(`http://localhost:5000/api/profile/photo/${data.user.id}`);
        } else {
          setProfilePhoto(null);
        }
      } else if (response.status === 401) {
        // Token is invalid, redirect to login
        localStorage.removeItem('token');
        setIsAuth(false);
        navigate('/');
      } else {
        console.error('Failed to fetch user data');
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showLogout && !event.target.closest('.profile-dropdown')) {
        setShowLogout(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogout]);

  // Fetch user data from database on mount
  useEffect(() => {
    const loadUserData = async () => {
      try {
        setIsLoading(true);
        await fetchUserData();
      } finally {
        setIsLoading(false);
      }
    };

    loadUserData();
  }, []);

  // Refresh user data when returning from other pages
  useEffect(() => {
    const handleFocus = () => {
      // Refresh data when window gains focus (user returns from another page)
      const token = localStorage.getItem('token');
      if (token) {
        fetchUserData();
      }
    };

    window.addEventListener('focus', handleFocus);
    return () => window.removeEventListener('focus', handleFocus);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };

  const handleSettings = () => {
    // Handle settings navigation
    console.log('Settings clicked');
  };

  const handleInbox = () => {
    console.log('Inbox clicked');
    // Navigate to inbox or show inbox content
  };

  const handleSpam = () => {
    console.log('Spam clicked');
    // Navigate to spam folder
  };

  const handleSent = () => {
    console.log('Sent clicked');
    // Navigate to sent items
  };

  const handleDrafts = () => {
    console.log('Drafts clicked');
    // Navigate to drafts folder
  };

  const goToCompose = () => {
    navigate('/compose');
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`app ${darkMode ? 'dark' : 'light'}`}>
      <nav className="navbar">
        <div className="navbar-title">
          <h1>CursorMail</h1>
        </div>
        
        <div className="navbar-buttons">
          <button className="navbar-icon" onClick={handleInbox} title="Inbox">
            <FontAwesomeIcon icon={faInbox} />
          </button>
          <button className="navbar-icon" onClick={handleSpam} title="Spam">
            <FontAwesomeIcon icon={faBan} />
          </button>
          <button className="compose-button" onClick={goToCompose}>
            Compose
          </button>
          <button className="navbar-icon" onClick={handleSent} title="Sent">
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
          <button className="navbar-icon" onClick={handleDrafts} title="Drafts">
            <FontAwesomeIcon icon={faFileAlt} />
          </button>
        </div>

        <div className="profile-container">
          <div className="profile-info" onClick={() => setShowLogout(!showLogout)}>
            <img
              src={profilePhoto || require('./images/profile.jfif')}
              alt="Profile"
              className="profile-image"
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'block';
              }}
            />
            <FontAwesomeIcon
              icon={faCircleUser}
              className="profile-fallback"
              style={{ display: profilePhoto ? 'none' : 'block' }}
            />
            <h4 className="profile-name">{isLoading ? 'Loading...' : (username || 'User')}</h4>
          </div>

          {showLogout && (
            <div className={`logout-dropdown ${darkMode ? 'dark' : 'light'}`}>
              <div className="profile-header">
                <img
                  src={profilePhoto || require('./images/profile.jfif')}
                  alt="Profile"
                  className="profile-image"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'block';
                  }}
                />
                <FontAwesomeIcon
                  icon={faCircleUser}
                  className="profile-fallback"
                  style={{ display: profilePhoto ? 'none' : 'block' }}
                />
                <h4 className="profile-name">{isLoading ? 'Loading...' : (username || 'User')}</h4>
              </div>

              <div className="menu-items">
                <div
                  className="menu-item"
                  onMouseEnter={() => setHoveredItem('profile')}
                  onMouseLeave={() => setHoveredItem(null)}
                  onClick={handleEditProfile}
                >
                  <FontAwesomeIcon icon={faUser} />
                  <span>My Profile</span>
                </div>

                <div
                  className="menu-item"
                  onMouseEnter={() => setHoveredItem('settings')}
                  onMouseLeave={() => setHoveredItem(null)}
                  onClick={handleSettings}
                >
                  <FontAwesomeIcon icon={faGear} />
                  <span>Settings</span>
                </div>

                <div
                  className="toggle-item"
                  onMouseEnter={() => setHoveredItem('dark')}
                  onMouseLeave={() => setHoveredItem(null)}
                >
                  <div className="toggle-content">
                    <FontAwesomeIcon icon={faMoon} />
                    <span>Switch to Dark</span>
                  </div>
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={darkMode}
                      onChange={toggleDarkMode}
                    />
                    <span className="slider"></span>
                  </label>
                </div>

                <div
                  className="menu-item"
                  onMouseEnter={() => setHoveredItem('logout')}
                  onMouseLeave={() => setHoveredItem(null)}
                  onClick={handleLogout}
                >
                  <FontAwesomeIcon icon={faSignOutAlt} />
                  <span>Log Out</span>
                </div>
              </div>

              <div className="dropdown-footer">
                <a href="/privacy">Privacy Policy</a>
                <span>•</span>
                <a href="/terms">Terms of Service</a>
              </div>
            </div>
          )}
        </div>
      </nav>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search emails..."
          className="search-bar"
        />
      </div>

      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/compose" element={<div>Compose Email Page</div>} />
        <Route path="/edit-profile" element={<EditProfile setIsAuth={setIsAuth} />} />
      </Routes>
    </div>
  );
};

export default Home;
