import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { EnvelopeIcon, LockClosedIcon, UserIcon } from '@heroicons/react/24/outline';
import './Register.css';
import image1 from "./images/standing-9@2x.png";
import image2 from "./images/standing-10.png";
import image3 from "./images/Message circle.png";
import image4 from "./images/Message square.png";

export default function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/register', { username, password, email });
      navigate('/');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Error');
    }
  };

  return (
    <div className="register-page">
      <div className="register-left">
        <div className="register-content">
          <h1 className="welcome-message">Create Account</h1>
              <div className="input-with-icon">
                <UserIcon className="input-icon" />
                <input 
                  id="username"
                  type="text" 
                  placeholder="Username" 
                  value={username} 
                  onChange={e => setUsername(e.target.value)} 
                  required 
                />
              </div>



              <div className="input-with-icon">
                <EnvelopeIcon className="input-icon" />
                <input 
                  id="email"
                  type="email" 
                  placeholder="Email" 
                  value={email} 
                  onChange={e => setEmail(e.target.value)} 
                  required 
                />
              </div>



              <div className="input-with-icon">
                <LockClosedIcon className="input-icon" />
                <input 
                  id="password"
                  type="password" 
                  placeholder="Password" 
                  value={password} 
                  onChange={e => setPassword(e.target.value)} 
                  required 
                />
              </div>


            <button type="submit" className="register-btn" onSu>Create Account</button>


          <div className="login-link">
            Already have an account? <Link to="/">Login</Link>
          </div>

          {msg && <div className="error-message">{msg}</div>}
        </div>
      </div>

      <div className="register-right">
        <div className="image-container">
          <div className="main-image">
            <img src={image1} className="standing-image" alt="Standing person" />
            <div className="floating-image image-1">
              <img src={image2} alt="Feature 1" />
            </div>
            <div className="floating-image image-2">
              <img src={image3} alt="Feature 2" />
            </div>
            <div className="floating-image image-3">
              <img src={image4} alt="Feature 3" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
