import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import Dashboard from './Dashboard';
import './App.css';

function Navbar({ isAuth, setIsAuth }) {
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };
  return (
    <nav style={{
      width: '100%', background: '#fff', boxShadow: '0 2px 8px rgba(0,0,0,0.04)', padding: '1rem 2rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 32
    }}>
      <span style={{ fontWeight: 700, fontSize: 22, color: '#f57c00', letterSpacing: 1 }}>CursorMail</span>
      {isAuth && <button style={{ background: '#f57c00', color: '#fff', border: 'none', borderRadius: 6, padding: '0.5rem 1.2rem', fontWeight: 600, cursor: 'pointer' }} onClick={handleLogout}>Logout</button>}
    </nav>
  );
}

function App() {
  const [isAuth, setIsAuth] = useState(!!localStorage.getItem('token'));

  useEffect(() => {
    const onStorage = () => setIsAuth(!!localStorage.getItem('token'));
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  return (
    <div className="app-container">
      <BrowserRouter>
        <Navbar isAuth={isAuth} setIsAuth={setIsAuth} />
        <Routes>
          <Route path="/" element={isAuth ? <Navigate to="/dashboard" /> : <Login setIsAuth={setIsAuth} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={isAuth ? <Dashboard setIsAuth={setIsAuth} /> : <Navigate to="/" />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
