import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import Dashboard from './Dashboard';
import profile from './images/profile.jpg';
import Home from './Home';
import './App.css';

function Navbar({ isAuth, setIsAuth }) {
  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };
  return (
    <nav className="navbar">
      <span className="navbar-title">CursorMail</span>
      {/* Search Bar */}
        <div className="flex-1 mx-10 max-w-xl">
          <input
            type="text"
            placeholder="Search..."
            className="w-full px-4 py-2 border rounded-md"
          />
          <button className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md">Search</button>
        </div>

        {/* Profile Avatar */}
        <img
          src={profile}
          alt="Profile"
          className="w-10 h-10 rounded-full"
        />
      {isAuth && <button className="navbar-logout-btn" onClick={handleLogout}>Logout</button>}

    </nav>
  );
}

function App() {
  const [isAuth, setIsAuth] = useState(!!localStorage.getItem('token'));

  useEffect(() => {
    const onStorage = () => setIsAuth(!!localStorage.getItem('token'));
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  return (
    <div className="app-container">
      <BrowserRouter>
        <Navbar isAuth={isAuth} setIsAuth={setIsAuth} />
        <Routes>
          <Route
            path="/"
            element={isAuth ? <Navigate to="/home" /> : <Login setIsAuth={setIsAuth} />}
          />
          <Route path="/register" element={<Register />} />

          {/* New Home Route */}
          <Route
            path="/home"
            element={isAuth ? <Home setIsAuth={setIsAuth} /> : <Navigate to="/" />}
          />
        </Routes>


      </BrowserRouter>
    </div>
  );
}

export default App;
