import React, { useState, useEffect } from 'react';
import {
  faUser,
  faEnvelope,
  faGear,
  faMoon,
  faSignOutAlt,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePlus, faCircleUser } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';
// Assuming you have a profile image in the images folder

const Home = ({ setIsAuth }) => {
  const [darkMode, setDarkMode] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuth(false);
    navigate('/');
  };
  const handleEditProfile = () => {
    navigate('/edit-profile');
  };
  const goToCompose = () => {
    navigate('/dashboard');
    <BrowserRouter>
      <Routes>
            <Route element={isAuth ? <Dashboard setIsAuth={setIsAuth} /> : <Navigate to="/" />}></Route> // or your /compose page if you have one
      </Routes>
      </BrowserRouter>
  };
  const navigate = useNavigate();
  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="navbar">
        <span className="navbar-title">CursorMail</span>
        <div className="navbar-buttons">
          <div className="compose-button" onClick={goToCompose} title="Compose"><FontAwesomeIcon icon={faSquarePlus} /></div>
        </div>
        <div className="profile-container">
          <FontAwesomeIcon icon={faCircleUser} alt="Profile"
            className="profile-avatar"
            onClick={() => setShowLogout(!showLogout)} />
          {showLogout && (
            <div className="logout-dropdown">
              <button onClick={handleEditProfile} className="edit-profile-button">
                Edit Profile
              </button>
              <button onClick={handleLogout} className="logout-button">
                Logout
              </button>
            </div>
          )}
        </div>

      </nav>
      <header className="flex justify-between items-center px-6 py-4 bg-white shadow">
        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."
            className="search-input"
          />
          <button className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-md">Search</button>
        </div>
      </header>

      {/* Dashboard Section */}
      <main className="p-6">
        <h2 className="text-xl font-semibold mb-4">Dashboard</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <div className="bg-white p-4 shadow rounded-lg">Widget 1</div>
          <div className="bg-white p-4 shadow rounded-lg">Widget 2</div>
          <div className="bg-white p-4 shadow rounded-lg">Widget 3</div>
        </div>
      </main>
    </div>
  );
}

export default Home;
